import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router";
import Navbar from "../components/Navbar";
import { apiUrl } from "../config/config";
import "../css/Cart.css";

const Cart = () => {
  const navigate = useNavigate();
  const [cart, setCart] = useState([]);
  const [totalPrice, setTotalPrice] = useState(0);
  const [error, setError] = useState(null);
  const [message, setMessage] = useState(null);
  const [errors, setErrors] = useState({});
  const [formData, setFormData] = useState({
    pincode: "",
    state: "",
    city: "",
    street: "",
  });
  const [formError, setFormError] = useState(null);
  const [isPincodeValid, setIsPincodeValid] = useState(false);

  useEffect(() => {
    const checkStatus = async () => {
      try {
        const response = await fetch(`${apiUrl}/isLoggedIn`, {
          method: "GET",
          credentials: "include",
          headers: {
            "Content-Type": "application/json",
          },
        });
        if (response.ok) {
          fetchCart();
        } else {
          navigate('/login');
        }
      } catch (error) {
        console.error(error);
        navigate('/notfound');
      }
    };
    checkStatus();
  }, [navigate]);

  const fetchCart = async () => {
    try {
      const response = await fetch(`${apiUrl}/display-cart`, {
        method: "GET",
        credentials: "include",
        headers: {
          "Content-Type": "application/json"
        }
      });
      const data = await response.json();
      if (response.ok) {
        setTotalPrice(data.totalPrice);
        setCart(data.cart);
        setError(null);
        const newErrors = {};
        data.cart.forEach(row => {
          if (row.quantity > row.stock_quantity) {
            newErrors[row.item_id] = "Exceeds stock quantity";
          }
        });
        setErrors(newErrors);
      } else {
        setError(data.message);
      }
      setMessage(data.message);
    } catch (error) {
      console.error(error);
      navigate('/notfound');
    }
  };

  const handlePinCodeChange = async () => {
    if (!formData.pincode || formData.pincode.length === 0) {
      setFormError("Pincode is required");
      setIsPincodeValid(false);
      setFormData(prev => ({
        ...prev,
        state: "",
        city: ""
      }));
      return false;
    }

    try {
      const response = await fetch(`${apiUrl}/fetch-pincode?pincode=${formData.pincode}`, {
        method: "GET",
        credentials: "include",
        headers: {
          "Content-Type": "application/json"
        }
      });

      const data = await response.json();

      if (response.ok) {
        setFormData(prev => ({
          ...prev,
          state: data.state,
          city: data.name
        }));
        setFormError(null);
        setIsPincodeValid(true);
        return true;
      } else {
        setFormError("Invalid PINCODE");
        setIsPincodeValid(false);
        setFormData(prev => ({
          ...prev,
          state: "",
          city: ""
        }));
        return false;
      }
    } catch (error) {
      console.error(error);
      setFormError("Error validating pincode");
      setIsPincodeValid(false);
      navigate("/notfound");
      return false;
    }
  };

  const handleCheckout = async (e) => {
    e.preventDefault();

    // First validate pincode
    const isPincodeValidated = await handlePinCodeChange();
    if (!isPincodeValidated) {
      setMessage("Please enter a valid pincode");
      return;
    }

    // Then check all required fields
    if (!formData.pincode || !formData.street || !formData.city || !formData.state) {
      setMessage("Please fill in all address fields");
      return;
    }

    try {
      const response = await fetch(`${apiUrl}/place-order`, {
        method: "POST",
        credentials: "include",
        headers: {
          "Content-Type": "application/json"
        },
        body: JSON.stringify({
          city: formData.city,
          state: formData.state,
          pincode: formData.pincode,
          street: formData.street
        })
      });

      const data = await response.json();

      if (response.ok) {
        navigate("/order-confirmation");
      } else {
        setError(data.message);
      }
    } catch (error) {
      console.error(error);
      navigate("/notfound");
    }
  };

  // ... rest of your component code (updateQuantity, removeFromCart, etc.) ...

  return (
    <>
      <Navbar />
      <div className="cart-container">
        <h1>Your Cart</h1>
        {message && <div className="cart-message">{message}</div>}
        {cart.length === 0 ? (
          <p className="empty-cart-message">Your cart is empty</p>
        ) : (
          <>
            {/* ... cart table code ... */}
            <form onSubmit={handleCheckout}>
              <div>
                <label>Pincode:</label>
                <input
                  type="text"
                  name="pincode"
                  value={formData.pincode}
                  onChange={(e) => {
                    setFormData(prev => ({
                      ...prev,
                      pincode: e.target.value
                    }));
                    setIsPincodeValid(false);
                  }}
                  onBlur={handlePinCodeChange}
                  placeholder="Enter pincode"
                  required
                />
                {formError && <p className="error-message">{formError}</p>}
              </div>

              <div>
                <label>Street:</label>
                <input
                  type="text"
                  name="street"
                  value={formData.street}
                  onChange={(e) => setFormData(prev => ({
                    ...prev,
                    street: e.target.value
                  }))}
                  placeholder="Enter street"
                  required
                />
              </div>

              <div>
                <label>City:</label>
                <input
                  type="text"
                  name="city"
                  value={formData.city}
                  placeholder="City will be populated automatically"
                  readOnly
                />
              </div>

              <div>
                <label>State:</label>
                <input
                  type="text"
                  name="state"
                  value={formData.state}
                  placeholder="State will be populated automatically"
                  readOnly
                />
              </div>

              <div className="cart-total">
                <h3>Total: ${totalPrice}</h3>
                <button
                  type="submit"
                  disabled={!isPincodeValid || cart.length === 0}
                >
                  Proceed to Checkout
                </button>
              </div>
            </form>
          </>
        )}
      </div>
    </>
  );
};

export default Cart;
